mapper.select:
select count(*)
from setmeal
where category_id = #{id}

select setmeal.*,c.name as categoryName from setmeal left join category c on setmeal.category_id = c.id
setmeal.name like "%"#{name}"%"
and setmeal.category_id =#{categoryId}
and setmeal.status =#{status}
order by setmeal.create_time desc
